import React from 'react'

const Icon = ({ icon = 'error', size = 1 }) => (
  <i className={`xo-icon-${icon} ${Number.isInteger(size) ? `fa-${size}x` : `fa-${size}`}`}></i>
)
Icon.propTypes = {
  icon: React.PropTypes.string,
  size: React.PropTypes.oneOfType([
    React.PropTypes.string,
    React.PropTypes.number
  ])
}
export default Icon
